import React from 'react';
import { useState,useEffect,useRef } from 'react';
import logo from '../../images/logo.png';
import '../Navbar/Navbar.css';

const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const menuRef = useRef(null);
  
    const toggleMenu = () => {
        setIsOpen((prevState) => !prevState);
      };
  
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsOpen(false);
        setSelectedItem(null);
      }
    };
  
    const handleItemClick = (item) => {
      setSelectedItem(item);
    };
  
    useEffect(() => {
      document.addEventListener('click', handleClickOutside);
      return () => {
        document.removeEventListener('click', handleClickOutside);
      };
    }, []);

  return (
    <nav className='nav'>
      <div className="logo">
        <a href="/"><img id='logo' src={logo} alt="Logo" /></a>
      </div>
      <ul className="nav-links">
        <li><button id='contact'>Contact Us</button></li>
        <li><button id='menu' onClick={toggleMenu}>Menu</button></li>
        {isOpen && (
        <div ref={menuRef} className='links'>
          <ul>
            <li onClick={() => handleItemClick('Home')}>Home</li>
            <li onClick={() => handleItemClick('About')}>About</li>
            <li onClick={() => handleItemClick('Contact')}>Contact</li>
          </ul>
          {selectedItem && selectedItem === 'Home' && (
            <ul>
              <li>Sub Item 1</li>
              <li>Sub Item 2</li>
            </ul>
          )}
          {selectedItem && selectedItem === 'About' && (
            <ul>
              <li>Sub Item A</li>
              <li>Sub Item B</li>
            </ul>
          )}
        </div>
      )}
      </ul>
    </nav>
  )
}

export default Navbar;
