import React, { useEffect } from 'react';
import { neonCursor } from 'threejs-toys';
import './App.css';
import Footer from './Components/Footer';
import Home from './pages/Home';
import Navbar from './Components/Navbar';


const App = () => {
  useEffect(() => {
    const initNeonCursor = () => {
      neonCursor({
        el: document.getElementById('app'),
        shaderPoints: 16,
        curvePoints: 80,
        curveLerp: 0.5,
        radius1: 5,
        radius2: 30,
        velocityTreshold: 10,
        sleepRadiusX: 100,
        sleepRadiusY: 100,
        sleepTimeCoefX: 0.0025,
        sleepTimeCoefY: 0.0025,
      });
    };

    initNeonCursor();
  }, []);

  const appStyles = {
    backgroundSize: 'cover', 
    margin: 0,
    width: '100vw',
    height: '100vh',
    overflow: 'hidden',
    touchAction: 'pan-up',
    color: '#ffffff',
    fontFamily: 'Montserrat, sans-serif',
    textAlign: 'center',
    textShadow: '0 0 5px #ffffff, 0 0 20px #000, 0 0 30px #000',
  };

  // const h1Styles = {
  //   '--fontSize': '60px',
  //   '--lineHeight': '80px',
  //   width: 'auto',
  //   height: 'calc(2 * var(--lineHeight))',
  //   lineHeight: 'var(--lineHeight)',
  //   margin: 'calc(50vh - var(--lineHeight)) auto 0',
  //   fontSize: 'var(--fontSize)',
  //   textTransform: 'uppercase',
  // };

  // const aStyles = {
  //   marginTop: '10px',
  //   display: 'inline-block',
  //   textDecoration: 'none',
  //   color: '#fff',
  // };

  return (
    <div id="app" style={appStyles}>
      <div id="hero">
            <Navbar />
            <Home />
            <Footer />
        {/* <h1 style={h1Styles}>
          NEON<br />CURSOR
        </h1> */}
        {/* <a target="_blank" href="https://github.com/klevron/threejs-toys" style={aStyles}>
          github/threejs-toys
        </a> */}
      </div>
    </div>
  );
};

export default App;


